CREATE TABLE `ultracaddy`.`t_force_update` ( `id` INT(20) NOT NULL AUTO_INCREMENT , `version` VARCHAR(20) NOT NULL COMMENT 'バージョン' , `title` VARCHAR(128) NOT NULL COMMENT 'タイトル' , `body` TEXT NOT NULL COMMENT '内容' , `status` TINYINT(2) NULL DEFAULT '0' COMMENT 'ステータス ０：無効 １：有効 ' , `create_by` INT(20) NULL , `created` DATETIME NULL COMMENT '作成日時' , `update_by` INT(20) NULL , `updated` DATETIME NULL COMMENT '更新日時' , `delete_flg` TINYINT(2) NOT NULL DEFAULT '0' COMMENT '0: 公開中 1: 削除済 ' , PRIMARY KEY (`id`)) ENGINE = InnoDB;